<?php


namespace Tests\AppBundle\Controller\API;

use Liip\FunctionalTestBundle\Test\WebTestCase;
use Doctrine\ORM\Tools\SchemaTool;
use Symfony\Component\HttpFoundation\File\UploadedFile;

class PackageControllerTest extends WebTestCase
{
    // Delete folder --> From nbari at dalmp dot com http://php.net/manual/en/function.rmdir.php#110489
    private function delTree($dir) {
        $files = array_diff(scandir($dir), array('.','..'));
        foreach ($files as $file) {
            (is_dir("$dir/$file")) ? $this->delTree("$dir/$file") : unlink("$dir/$file");
        }
        return rmdir($dir);
    }

    // Set up database with fixtures
    public function setUp() {

        $em = $this->getContainer()->get('doctrine')->getManager();

        if (!isset($metadatas)) {
            $metadatas = $em->getMetadataFactory()->getAllMetadata();
        }

        $schemaTool = new SchemaTool($em);

        $schemaTool->dropDatabase();

        if (!empty($metadatas)) {
            $schemaTool->createSchema($metadatas);
        }

        $this->postFixtureSetup();

        $this->loadFixtures(array(
            'AppBundle\DataFixtures\ORM\LoadVendor',
            'AppBundle\DataFixtures\ORM\LoadShipper',
            'AppBundle\DataFixtures\ORM\LoadReceiver',
            'AppBundle\DataFixtures\ORM\LoadPackage',
        ));
    }

    public function tearDown() {
        // Remove files (will remove all files in current date's folder!)
        $currentDate = new \DateTime("NOW");
        $dateFolder = $currentDate->format('Ymd');

        $dirRoot = $this->getContainer()->get('kernel')->getRootDir() . '/../';
        $path = "upload/" . $dateFolder . "/";

        if (is_dir($dirRoot . $path)) {
            $this->delTree($dirRoot . $path);
        }

    }

    public function testNewPackageRoute() {
        echo __METHOD__ . "\n";

        $client = static::createClient();

        // Assert that a new package with no pictures/file attachment is successfully created
        $client->request('POST', '/packages/new', array(
            "trackingNumber" => "testPackage",
            "numberOfPackages" => 4,
            "shipperId" => 1,
            "receiverId" => 1,
            "vendorId" => 1
        ));

        // Testing response code for /packages/new
        $this->assertTrue($client->getResponse()->isSuccessful());

        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $npNoImgNoFile = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $npNoImgNoFile);
        $this->assertEquals('success', $npNoImgNoFile['result']);
        $this->assertArrayHasKey('message', $npNoImgNoFile);
        $this->assertArrayHasKey('object', $npNoImgNoFile);
        $this->assertNotEmpty($npNoImgNoFile['object']);
        $this->assertEquals(4, $npNoImgNoFile['object']['numberOfPackages']);
        $this->assertEquals('testPackage', $npNoImgNoFile['object']['trackingNumber']);
        $this->assertEquals('fixtureShipper', $npNoImgNoFile['object']['shipper']['name']);
        $this->assertEquals('fixtureReceiver', $npNoImgNoFile['object']['receiver']['name']);
        $this->assertEquals('fixtureVendor', $npNoImgNoFile['object']['vendor']['name']);


        // Assert that a new package with pictures/no file attachments is successfully created
        $client->request('POST', '/packages/new', array(
            "trackingNumber" => "testPackage2",
            "numberOfPackages" => 4,
            "shipperId" => 1,
            "receiverId" => 1,
            "vendorId" => 1,
            "packingSlipPictures" => [
                "data:image/png;base64,",
                "data:image/png;base64,"
            ]
        ));
        // Testing response code for /packages/new
        $this->assertTrue($client->getResponse()->isSuccessful());


        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $npImgNoFile = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $npImgNoFile);
        $this->assertEquals('success', $npImgNoFile['result']);
        $this->assertArrayHasKey('message', $npImgNoFile);
        $this->assertArrayHasKey('object', $npImgNoFile);
        $this->assertNotEmpty($npImgNoFile['object']);
        $this->assertEquals(4, $npImgNoFile['object']['numberOfPackages']);
        $this->assertEquals('testPackage2', $npImgNoFile['object']['trackingNumber']);
        $this->assertEquals('fixtureShipper', $npImgNoFile['object']['shipper']['name']);
        $this->assertEquals('fixtureReceiver', $npImgNoFile['object']['receiver']['name']);
        $this->assertEquals('fixtureVendor', $npImgNoFile['object']['vendor']['name']);
        $this->assertCount(2, $npImgNoFile['object']['packingSlips']);

        // Copy
        copy($this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test1.pdf", $this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test1_copy.pdf");
        copy($this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test2.pdf", $this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test2_copy.pdf");

        // Assert that a new package with no pictures/file attachments is successfully created
        $uploadedFile1 = new UploadedFile($this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test1_copy.pdf", "test1_copy.pdf");
        $uploadedFile2 = new UploadedFile($this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test2_copy.pdf", "test2_copy.pdf");

        $client->request('POST', '/packages/new', array(
            "trackingNumber" => "testPackage3",
            "numberOfPackages" => 4,
            "shipperId" => 1,
            "receiverId" => 1,
            "vendorId" => 1,
        ), array(
            "attachedPackingSlips" => [$uploadedFile1, $uploadedFile2]
        ));

        // Testing response code for /packages/new
        $this->assertTrue($client->getResponse()->isSuccessful());

        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $npNoImgFile = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $npNoImgFile);
        $this->assertEquals('success', $npNoImgFile['result']);
        $this->assertArrayHasKey('message', $npNoImgFile);
        $this->assertArrayHasKey('object', $npNoImgFile);
        $this->assertNotEmpty($npNoImgFile['object']);
        $this->assertEquals(4, $npNoImgFile['object']['numberOfPackages']);
        $this->assertEquals('testPackage3', $npNoImgFile['object']['trackingNumber']);
        $this->assertEquals('fixtureShipper', $npNoImgFile['object']['shipper']['name']);
        $this->assertEquals('fixtureReceiver', $npNoImgFile['object']['receiver']['name']);
        $this->assertEquals('fixtureVendor', $npNoImgFile['object']['vendor']['name']);
        $this->assertCount(2, $npNoImgFile['object']['packingSlips']);

        // Assert that a new package with the same tracking number returns error
        $client->request('POST', '/packages/new', array(
            "trackingNumber" => "testPackage",
            "numberOfPackages" => 4,
            "shipperId" => 1,
            "receiverId" => 1,
            "vendorId" => 1
        ));

        $this->assertTrue($client->getResponse()->isSuccessful());

        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $duplicatePackage = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $duplicatePackage);
        $this->assertArrayHasKey('message', $duplicatePackage);
        $this->assertArrayHasKey('object', $duplicatePackage);
        $this->assertEmpty($duplicatePackage['object']);
        $this->assertEquals('error', $duplicatePackage['result']);

        // Assert that a new package with incorrect or invalid tokens return error
        $client->request('POST', '/packages/new', array(
            "trackingNumber" => "testPackage4",
            "numberOfPackages" => null,
            "receiverId" => 1,
            "vendorId" => 1
        ));

        $this->assertTrue($client->getResponse()->isSuccessful());

        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $errorParamsResponse = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $errorParamsResponse);
        $this->assertArrayHasKey('message', $errorParamsResponse);
        $this->assertArrayHasKey('object', $errorParamsResponse);
        $this->assertNotEmpty($errorParamsResponse['object']);
        $this->assertEquals('error', $errorParamsResponse['result']);

        // Assert that a new package with incorrect tokens return error
        $client->request('POST', '/packages/new', array(
            "trackingNumber" => "testPackage",
            "numberOfPackages" => "",
            "shipperId" => 1,
            "receiverId" => 1,
            "vendorId" => 1
        ));

        $this->assertTrue($client->getResponse()->isSuccessful());

        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $errorParams2Package = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $errorParams2Package);
        $this->assertArrayHasKey('message', $errorParams2Package);
        $this->assertArrayHasKey('object', $errorParams2Package);
        $this->assertEmpty($errorParams2Package['object']);
        $this->assertEquals('error', $errorParams2Package['result']);
    }

    public function testUpdatePackageRoute() {
        echo __METHOD__ . "\n";

        $client = static::createClient();

        // Copy
        copy($this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test1.pdf", $this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test1_copy.pdf");
        copy($this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test2.pdf", $this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test2_copy.pdf");

        $uploadedFile1 = new UploadedFile($this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test1_copy.pdf", "test1_copy.pdf");
        $uploadedFile2 = new UploadedFile($this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test2_copy.pdf", "test2_copy.pdf");

        // Test for update (adding packing slips)
        $client->request('POST', '/packages/fixturePackage/update', array(
            "numberOfPackages" => 1,
            "packingSlipPictures" => [
                "data:image/png;base64,",
                "data:image/png;base64,"
            ]
        ), array(
            "attachedPackingSlips" => [$uploadedFile1, $uploadedFile2]
        ));

        $this->assertTrue($client->getResponse()->isSuccessful());

        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $updatedPackage = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $updatedPackage);
        $this->assertArrayHasKey('message', $updatedPackage);
        $this->assertArrayHasKey('object', $updatedPackage);
        $this->assertNotEmpty($updatedPackage['object']);
        $this->assertEquals('success', $updatedPackage['result']);
        $this->assertEquals(1, $updatedPackage['object']['numberOfPackages']);
        $this->assertCount(4, $updatedPackage['object']['packingSlips']);

        // Copy
        copy($this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test1.pdf", $this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test1_copy.pdf");
        copy($this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test1.pdf", $this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test1_copy2.pdf");

        $uploadedFile3 = new UploadedFile($this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test1_copy.pdf", "test1_copy.pdf");
        $uploadedFile4 = new UploadedFile($this->getContainer()->get("kernel")->getRootDir() . "/../tests/UploadedFiles/test1_copy2.pdf", "test1_copy2.pdf");

        // Test for update (removing some files and adding some with duplicate)
        $client->request('POST', '/packages/fixturePackage/update', array(
            "numberOfPackages" => 1,
            "deletePackingSlipIds" => array(
                1, 3
            )
        ), array(
            "attachedPackingSlips" => [$uploadedFile3, $uploadedFile4]
        ));

        $this->assertTrue($client->getResponse()->isSuccessful());

        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $removePackingSlipsPackage = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $removePackingSlipsPackage);
        $this->assertArrayHasKey('message', $removePackingSlipsPackage);
        $this->assertArrayHasKey('object', $removePackingSlipsPackage);
        $this->assertNotEmpty($removePackingSlipsPackage['object']);
        $this->assertEquals('success', $removePackingSlipsPackage['result']);
        $this->assertEquals(1, $removePackingSlipsPackage['object']['numberOfPackages']);
        $this->assertCount(4, $removePackingSlipsPackage['object']['packingSlips']);

        // Test for update (removing all files)
        $client->request('POST', '/packages/fixturePackage/update', array(
            "numberOfPackages" => 1,
            "deletePackingSlipIds" => array(
                2, 4, 5, 6
            )
        ));

        $this->assertTrue($client->getResponse()->isSuccessful());

        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $removePackingSlipsPackage = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $removePackingSlipsPackage);
        $this->assertArrayHasKey('message', $removePackingSlipsPackage);
        $this->assertArrayHasKey('object', $removePackingSlipsPackage);
        $this->assertNotEmpty($removePackingSlipsPackage['object']);
        $this->assertEquals('success', $removePackingSlipsPackage['result']);
        $this->assertEquals(1, $removePackingSlipsPackage['object']['numberOfPackages']);
        $this->assertCount(0, $removePackingSlipsPackage['object']['packingSlips']);

        // Test for update (updating receiver, shipper and vendor)
        $client->request('POST', '/packages/fixturePackage/update', array(
            "numberOfPackages" => 1,
            "receiverId" => 2,
            "shipperId" => 2,
            "vendorId" => 2
        ));

        $this->assertTrue($client->getResponse()->isSuccessful());

        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $updateEntitiesPackage = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $updateEntitiesPackage);
        $this->assertArrayHasKey('message', $updateEntitiesPackage);
        $this->assertArrayHasKey('object', $updateEntitiesPackage);
        $this->assertNotEmpty($updateEntitiesPackage['object']);
        $this->assertEquals('success', $updateEntitiesPackage['result']);
        $this->assertEquals(1, $updateEntitiesPackage['object']['numberOfPackages']);
        $this->assertCount(0, $updateEntitiesPackage['object']['packingSlips']);

        
        // Test for errors
        $client->request('POST', '/packages/stuffedchickenwings/update', array(
            "numberOfPackages" => 1,
            "removedPackingSlipIds" => array(
                "removePackingSlipOne", "removePackingSlipTwo"
            )
        ));

        $this->assertTrue($client->getResponse()->isSuccessful());

        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $errorResponse = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $errorResponse);
        $this->assertArrayHasKey('message', $errorResponse);
        $this->assertArrayHasKey('object', $errorResponse);
        $this->assertEmpty($errorResponse['object']);
        $this->assertEquals('error', $errorResponse['result']);
    }

