    public function testDeliverPackageRoute() {
        echo __METHOD__ . "\n";

        $client = static::createClient();

        // Assert that package is delivered
        $client->request('PUT', '/packages/fixturePackage/deliver');

        $this->assertTrue($client->getResponse()->isSuccessful());

        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $deliveredPackage = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $deliveredPackage);
        $this->assertEquals('success', $deliveredPackage['result']);

        $this->assertArrayHasKey('message', $deliveredPackage);

        $this->assertArrayHasKey('object', $deliveredPackage);
        $this->assertNotEmpty($deliveredPackage['object']);
        $this->assertEquals(7, $deliveredPackage['object']['numberOfPackages']);
        $this->assertEquals('fixturePackage', $deliveredPackage['object']['trackingNumber']);
        $this->assertEquals(1, $deliveredPackage['object']['delivered']);

        // Assert that a package is already delivered returns an error
        $client->request('PUT', '/packages/fixturePackage/deliver');

        $this->assertTrue($client->getResponse()->isSuccessful());

        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $alreadyDeliveredPackage = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $alreadyDeliveredPackage);
        $this->assertEquals('error', $alreadyDeliveredPackage['result']);
        $this->assertArrayHasKey('message', $alreadyDeliveredPackage);
        $this->assertArrayHasKey('object', $alreadyDeliveredPackage);
        $this->assertEmpty($alreadyDeliveredPackage['object']);

        // Assert that a package that is not in the database returns error
        $client->request('PUT', '/packages/stuffedchickenwings/deliver');

        $this->assertTrue($client->getResponse()->isSuccessful());

        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $nonExistingPackage = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $nonExistingPackage);
        $this->assertEquals('error', $nonExistingPackage['result']);
        $this->assertArrayHasKey('message', $nonExistingPackage);
        $this->assertArrayHasKey('object', $nonExistingPackage);
        $this->assertEmpty($nonExistingPackage['object']);
    }

