    public function testGetPackagesRoute() {
        echo __METHOD__ . "\n";

        $client = static::createClient();

        // Assert that getting packages for today yields 2 packages
        $client->request('GET', '/packages', [
            'dateBegin' => "NOW",
            'dateEnd' => "NOW"
        ]);

        $this->assertTrue($client->getResponse()->isSuccessful());
        
        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $packagesResponse = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $packagesResponse);
        $this->assertArrayHasKey('message', $packagesResponse);
        $this->assertArrayHasKey('object', $packagesResponse);
        $this->assertCount(4, $packagesResponse['object']);
        $this->assertEquals('success', $packagesResponse['result']);

        // Assert that getting packages for any other same day yields 0 packages
        $client->request('GET', '/packages', [
            'dateBegin' => "03/18/2013",
            'dateEnd' => "03/18/2013"
        ]);

        $this->assertTrue($client->getResponse()->isSuccessful());

        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $errorSameDateResponse = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $errorSameDateResponse);
        $this->assertArrayHasKey('message', $errorSameDateResponse);
        $this->assertArrayHasKey('object', $errorSameDateResponse);
        $this->assertCount(0, $errorSameDateResponse['object']);
        $this->assertEquals('error', $errorSameDateResponse['result']);

        // Assert that getting packages for any other, different, dates yields 0 packages
        $client->request('GET', '/packages', [
            'dateBegin' => "03/18/2013",
            'dateEnd' => "03/19/2013"
        ]);

        $this->assertTrue($client->getResponse()->isSuccessful());

        $this->assertTrue(
            $client->getResponse()->headers->contains(
                'Content-Type',
                'application/json'
            )
        );

        $errorDiffDateResponse = json_decode($client->getResponse()->getContent(), true);

        $this->assertArrayHasKey('result', $errorDiffDateResponse);
        $this->assertArrayHasKey('message', $errorDiffDateResponse);
        $this->assertArrayHasKey('object', $errorDiffDateResponse);
        $this->assertCount(0, $errorDiffDateResponse['object']);
        $this->assertEquals('error', $errorDiffDateResponse['result']);
    }

